// ProjectEuler qid-8 - Find the thirteen adjacent digits in the 1000-digit number that have the greatest product. what is the value of this product?


let number = '';

let arrayNumber = Array.from(number, Number);

function product(digits) {
	let x,y = 0;
	let a = 1;
	let b = 0;
	for(x = 0; x < 1000 - digits; x++){
		for(y = x; y <= x + (digits - 1); y++){
			a = a * arrayNumber[y];
		}
		if(b < a) {b = a;}
		a = 1;
	}
	return b;
}
console.log(product(13));